import React, { Component } from 'react';
import { API_URL, API_KEY, IMAGE_BASE_URL, POSTER_SIZE, BACKDROP_SIZE } from '../../config';
import HeroImage from '../elements/HeroImage/Heroimage';
import SearchBar from '../elements/SearchBar/SearchBar';
import FourColGrid from '../elements/FourColGrid/FourColGrid';
import MovieThumb from '../elements//MovieThumb/MovieThumb';
import LoadMoreBtn from '../elements/LoadMoreBtn/LoadMorebtn';
import Spinner from '../elements/Spinner/Spinner';

import './Home.css';

class Home extends Component {

    // STATE OR DATA MANAGEMENT
    state = {
        movies: [],
        heroImage: null,
        loading: false,
        currentPage: 0,
        totalPages: 0,
        searchTerm: ''
    }

    // CONNECT TO API
    componentDidMount() {
        this.setState({ loading: true });
        const endpoint = `${API_URL}movie/popular?api_key=${API_KEY}&language=en-US&page1`;
        this.fetchItems(endpoint);
    }

    // Search TO API
    searchItems = (searchTerm) => {
        let endpoint = '';
        this.setState ({
            movies: [],
            loading: true,
            searchTerm
        })

        if (searchTerm === '') {
            endpoint = `${API_URL}movie/popular?api_key=${API_KEY}&language=en-US&page1`;
        } else {
            endpoint = `${API_URL}search/movie?api_key={API_KEY}&language=en-US&query=${searchTerm}`;
        }
        this.fetchItems(endpoint);
    }

    // Load TO API
    loadMoreItems = () =>{
        let endpoint = '';
        this.setState({ loading: true });

        if(this.state.searchTerm === '') {
            endpoint = `${API_URL}movie/popular?api_key=${API_KEY}&language=en-US&page=${this.state.currentPage + 1}`;
        } else {
            endpoint = `${API_URL}search/movie?api_key=${API_KEY}&language=en-US&query${this.state.searchTerm}&page=${this.state.currentPage + 1}`;
        }
        this.fetchItems(endpoint);
    }

    // GET DATA API TO Json
    fetchItems = (endpoint) => {
        fetch(endpoint)
            .then(result => result.json())
            .then(result => {
                // CEK DATA API
                // console.log(result);
                this.setState({
                    movies: [...this.state.movies, ...result.results],
                    heroImage: this.state.heroImage || result.results[0],
                    loading: false,
                    currentPage: result.page,
                    totalPages: result.total_pages
                 })
            })
    }

    render () {
        return (
            <div className="rmdb-home">
            {this.state.heroImage ?
            <div>
                <HeroImage 
                    image={`${IMAGE_BASE_URL}${BACKDROP_SIZE}/${this.state.heroImage.backdrop_path}`}
                    title={this.state.heroImage.original_title}
                    text={this.state.heroImage.overview}                
                />

                <SearchBar callback= { this.searchItems }/>
            </div> : null }
                <div className="rmdb-home-grid">
                    <FourColGrid 
                        header={this.searchItems ? 'Search Result' : 'Popular Movies'}
                        loading={this.state.loading}
                    >

                    {this.state.movies.map ( (element, i) => {
                        return <MovieThumb
                                key={i}
                                clickable={true}
                                image={element.poster_path ? `${IMAGE_BASE_URL}${POSTER_SIZE}/${element.poster_path}`: './image/no_image.jpg'}
                                movieId={element.id}
                                movieName={element.original_title}
                                />
                    })}
                    </FourColGrid>
                    {this.state.loading ? <Spinner /> : null}
                    {( this.state.currentPage <= this.state.totalPages && !this.state.loading ) ?

                        <LoadMoreBtn text="Load More" onClick={this.loadMoreItems} />
                        : null 
                    }
                </div>
            </div>
        )
    }

}

export default Home;
